svg.script:
function resetElements() {
    document.getElementById("p1.t1").style.opacity = 0;
    document.getElementById("p1.t2").style.opacity = 0;
    document.getElementById("p2.t1").style.opacity = 0;
}

function loadPage(n) {    
    switch (n) {
        case 1:
            setTimeout(() => {
                document.getElementById("p1.t1").style.transform = "scale(0.6) translateY(-8px)";
                document.getElementById("p1.t2").style.transform = "scale(0.6) translateY(-8px)";
            });

            setTimeout(() => {
                document.getElementById("p1.t1").style.opacity = 1;
                document.getElementById("p1.t1").style.transform = "scale(1) translateY(0)";
            }, 500);

            setTimeout(() => {
                document.getElementById("p1.t2").style.opacity = 1;
                document.getElementById("p1.t2").style.transform = "scale(1) translateY(0)";
            }, 600);
            break;

        case 2:
        setTimeout(() => {
                document.getElementById("p2.t1").style.transform = "scale(0.6) translateY(-8px)";
            });

            setTimeout(() => {
                document.getElementById("p2.t1").style.opacity = 1;
                document.getElementById("p2.t1").style.transform = "scale(1) translateY(0)";
            }, 500);
    }
}

let
    pagesN = 2,
    active = 1;

loadPage(active);
setInterval(() => {
    resetElements();
    if (active == pagesN) active = 1;
    else active++;
    loadPage(active);
}, 8000);
